import React, {Component} from 'react';

class SearchForm extends Component {


    //Event Handler for form submission. 1. prevent page refresh 2. t
    handleSubmit = (e) => {
        //Prevent page refresh
        e.preventDefault();
        //Take value from the ref attribute in input element and declare new url query as path
        let searchInput = this.search.value;
        let path = `/${searchInput}`
        //Perform Flickr search with OnSearch prop passing the value into search
        this.props.onSearch(searchInput)
        //Clear input field
        searchInput = ''
        //Redirect page by pushing url path into history, thereby directing user to new query
        this.props.history.push(path)
    }

    render() {
        return (
            <form className="search-form" onSubmit={this.handleSubmit}>
                <input type="search" name="search" placeholder="Search" required ref={(input)=> {this.search=input}}/>
                <button type="submit" className="search-button">
                <svg fill="#fff" height="24" viewBox="0 0 23 23" width="24" xmlns="http://www.w3.org/2000/svg">
                    <path d="M15.5 14h-.79l-.28-.27C15.41 12.59 16 11.11 16 9.5 16 5.91 13.09 3 9.5 3S3 5.91 3 9.5 5.91 16 9.5 16c1.61 0 3.09-.59 4.23-1.57l.27.28v.79l5 4.99L20.49 19l-4.99-5zm-6 0C7.01 14 5 11.99 5 9.5S7.01 5 9.5 5 14 7.01 14 9.5 11.99 14 9.5 14z"/>
                    <path d="M0 0h24v24H0z" fill="none"/>
                </svg>
                </button>
            </form>
        )
    }
}

export default SearchForm;
